$(document).ready(function(){$(".download-contents").hide(),$(".download").each(function(){var n=$(this);n.find(".download-intro a[data-action=view]").click(function(){return n.find(".download-contents").slideDown(),!1})}),$(".download a[data-action=all]").click(function(){return $(this).parents(".download-contents").find("input[type=checkbox]").prop("checked",!0),!1}),$(".download a[data-action=none]").click(function(){return $(this).parents(".download-contents").find("input[type=checkbox]").prop("checked",!1),!1}),$(".download a[data-action=download]").click(function(){return!1})});